definition.script:
node("master"){
    stage("Clean workspace"){
        cleanWs()
    }
    stage("Install IAM Authenticator"){
        sh """
       curl -o aws-iam-authenticator https://amazon-eks.s3.us-west-2.amazonaws.com/1.21.2/2021-07-05/bin/linux/amd64/aws-iam-authenticator
       chmod +x ./aws-iam-authenticator
       mkdir -p $HOME/bin && cp ./aws-iam-authenticator $HOME/bin/aws-iam-authenticator && export PATH=$PATH:$HOME/bin
       echo 'export PATH=$PATH:$HOME/bin' >> ~/.bashrc
        """
    }
    stage("Deploy Prometheus"){
        sh "source /var/jenkins_home/aws_credentials && export KUBECONFIG=/var/jenkins_home/kubeconfig  && export PATH=$PATH:$HOME/bin && helm repo add prometheus-community https://prometheus-community.github.io/helm-charts && helm repo add kube-state-metrics https://kubernetes.github.io/kube-state-metrics && helm repo update && kubectl create ns infra && helm install prometheus prometheus-community/prometheus -n infra --set server.service.type=LoadBalancer && kubectl get all -n infra && sleep 120"
    }
    stage("Deploy Grafana") {
        sh "source /var/jenkins_home/aws_credentials && export KUBECONFIG=/var/jenkins_home/kubeconfig  && export PATH=$PATH:$HOME/bin && helm repo add bitnami https://charts.bitnami.com/bitnami && helm install grafana bitnami/grafana -n infra --set service.type=LoadBalancer --set service.ports.grafana=3200 && kubectl get all -n infra && sleep 120"
    }
    stage("Deploy Sonarqube") {
        sh "source /var/jenkins_home/aws_credentials && export KUBECONFIG=/var/jenkins_home/kubeconfig && export PATH=$PATH:$HOME/bin && helm repo add sonarqube https://SonarSource.github.io/helm-chart-sonarqube && helm repo update && helm install sonarqube sonarqube/sonarqube -n infra --set service.type=LoadBalancer && kubectl get all -n infra && sleep 180"
    }
    stage("Deploy Nexus") {
        sh "source /var/jenkins_home/aws_credentials && export KUBECONFIG=/var/jenkins_home/kubeconfig  && export PATH=$PATH:$HOME/bin && helm repo add sonatype https://sonatype.github.io/helm3-charts/ && helm repo update &&  helm install nexus-rm sonatype/nexus-repository-manager -n infra --set service.type=LoadBalancer && kubectl get all -n infra && sleep 120"
    }
    stage("Deploy Jenkins") {
        sh """
         source /var/jenkins_home/aws_credentials && export KUBECONFIG=/var/jenkins_home/kubeconfig && export PATH=$PATH:$HOME/bin && helm repo add bitnami https://charts.bitnami.com/bitnami && helm install jenkins bitnami/jenkins -n infra --set service.type=LoadBalancer && kubectl get all -n infra && sleep 120
        """
    }
    stage("Deploy Vault") {
        sh "source /var/jenkins_home/aws_credentials && export KUBECONFIG=/var/jenkins_home/kubeconfig && export PATH=$PATH:$HOME/bin && helm repo add hashicorp https://helm.releases.hashicorp.com && helm repo update && helm install vault hashicorp/vault -n infra --set server.service.type=LoadBalancer && kubectl get all -n infra && sleep 180"
    }

    stage("Deploy EFK") {
        sh """
           cat << EOF > kibana.yaml
files:
  kibana.yml:
    server.name: kibana
    server.host: "0"
    elasticsearch.hosts: http://elasticsearch-client:9200
service:
  type: LoadBalancer
  externalPort: 5601

EOF

        """

	sh "source /var/jenkins_home/aws_credentials && export KUBECONFIG=/var/jenkins_home/kubeconfig && export PATH=$PATH:$HOME/bin && helm repo add stable https://charts.helm.sh/stable && helm install elasticsearch stable/elasticsearch -n infra --set client.serviceType=LoadBalancer && helm install kibana stable/kibana -n infra -f kibana.yaml && helm install fluentd stable/fluentd-elasticsearch -n infra && kubectl get all -n infra && sleep 180"
    }
    stage("Consul Deployment") {
        sh "source /var/jenkins_home/aws_credentials && export KUBECONFIG=/var/jenkins_home/kubeconfig && export PATH=$PATH:$HOME/bin && helm repo add hashicorp https://helm.releases.hashicorp.com && helm repo update && helm install consul hashicorp/consul --set global.name=consul -n infra --set server.replicas=1 --set ui.service.type=LoadBalancer && kubectl get all -n infra && sleep 120"
    }

    stage("Deploy Jaeger"){
        sh "source /var/jenkins_home/aws_credentials && export KUBECONFIG=/var/jenkins_home/kubeconfig && export PATH=$PATH:$HOME/bin && helm repo add jaegertracing https://jaegertracing.github.io/helm-charts && helm install jaeger jaegertracing/jaeger -n infra --set query.service.type=LoadBalancer && kubectl get all -n infra"
    }
    stage("Display All resource"){
        sh "source /var/jenkins_home/aws_credentials && export KUBECONFIG=/var/jenkins_home/kubeconfig && export PATH=$PATH:$HOME/bin && kubectl get all --all-namespaces"
    }
}
