definition.script:
node("master"){
    stage("Clean workspace"){
        cleanWs()
    }
    stage("Install IAM Authenticator"){
        sh """
       curl -o aws-iam-authenticator https://amazon-eks.s3.us-west-2.amazonaws.com/1.21.2/2021-07-05/bin/linux/amd64/aws-iam-authenticator
       chmod +x ./aws-iam-authenticator
       mkdir -p $HOME/bin && cp ./aws-iam-authenticator $HOME/bin/aws-iam-authenticator && export PATH=$PATH:$HOME/bin
       echo 'export PATH=$PATH:$HOME/bin' >> ~/.bashrc
        """
    }
    stage("Clone repo") {
     sh "cd /var/jenkins_home/ && git clone https://github.com/bhagirathcloud/app.git"
    }
    stage("Deploy GOAPP") {
        sh """
          cd /var/jenkins_home/app/go-k8s
          source /var/jenkins_home/aws_credentials && export KUBECONFIG=/var/jenkins_home/kubeconfig && export PATH=$PATH:$HOME/bin
          export KUBECONFIG=/var/jenkins_home/kubeconfig
          helm install goapp ./helm-chart/ -n infra
          kubectl get po -n infra | grep goapp
          kubectl get svc -n infra | grep go

          echo 'App is running on instance-ip:3000'
        """
    }
}
